import React from 'react';

import { PaginationButton } from './PaginationButton';

interface Props {
  page: number;
  setPage: any;
  pageCount: any;
  latestData: any;
}

// this is not a button. it's a paginator.
export const Paginator = (props: Props) => {
  const previousPageLabel: string = '<< Previous Page';
  const nextPageLabel = 'Next Page >>';

  return (
    <div className="row justify-content-center">
      <div className="col-auto mt-4 mb-3 mr-3">
        <PaginationButton
          onClick={() => props.setPage((old: any) => Math.max(old - 1, 0))}
          disabled={props.page === 1}
          previousPageLabel={previousPageLabel}
        />
        <span className="mr-4 ml-4 mt-3">Current Page: {props.page}</span>
        <PaginationButton
          onClick={() => props.setPage((old: any) => old + 1)}
          disabled={!props.latestData || props.page === props.pageCount}
          nextPageLabel={nextPageLabel}
        />
      </div>
    </div>
  );
};
